
import React from 'react';
import SignupForm from './SignupForm';
import UserDashboard from './UserDashboard';
import BookDashboard from './BookDashboard';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {
  const userId = 123;
  return (
    <Router>
    <div className="App">
      <h1>User Signup</h1>
      <SignupForm />
      <h1>Library Management System</h1>
      <UserDashboard userId={userId} />
      <BookDashboard />
      <Switch>
          <Route path="/app/book-dashboard" component={BookDashboard} />
          <Route path="/app/user-dashboard" component={UserDashboard} />
          <Route path="/app/signup-form" component={SignupForm} />
        </Switch>
    </div>
    </Router>
  );
}

export default App;
